import base64

# Your Base64 string (response from the API)
response = {
"chart": ""
}

chart_base64 = response["chart"]

# Ensure the Base64 string length is a multiple of 4
padding_needed = len(chart_base64) % 4
if padding_needed:
    chart_base64 += "=" * (4 - padding_needed)  # Add padding if necessary

# Decode the Base64 string
try:
    chart_binary = base64.b64decode(chart_base64)
    with open("example_images/investment_growth.png", "wb") as file:
        file.write(chart_binary)
    print("Chart saved as 'investment_growth.png'")
except base64.binascii.Error as e:
    print(f"Error decoding Base64: {e}")
